mapper.select:
SELECT phonenumber FROM sys_user WHERE status=0 and
phonenumber=#{phonenumber}
and user_id!=#{userId}

mapper.update:
update sys_user
dept_id = #{deptId}, user_number = #{userNumber}, branch_id = #{branchId}, login_name = #{loginName}, user_name = #{userName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, salt = #{salt}, status = #{status}, refuse_des = #{refuseDes}, update_by = #{updateBy}, update_time = sysdate()
where 1=1 and user_id = #{userId}
